function securityCheck(){jQuery("#errorInfoDiv").html("");var b=jQuery("#userName").val();var c=jQuery("#password").val();if(null==b||""==b||null==c||""==c){jQuery("#errorInfoDiv").html("用户名或密码不能为空！");return false}var a="/securityCheck";var e={"userName":b,"password":c};jQuery.post(a,e,function(d){if("success"==d){if("report"==b){go("/report")}else{go("/charge")}}else{jQuery("#errorInfoDiv").html(d)}})}function keyLogin(){if(13==event.keyCode){securityCheck()}}function go(a){if(document.all){document.write("<a id='goa' href='"+a+"' style='display:none;'>a</a>");document.getElementById("goa").click()}else{window.location=a}};